import React, { useState } from 'react';

const LoginForm = ({ onLogin }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    // const[action, setAction] = useState("Log in");

    const handleLogin = (e) => {
        e.preventDefault();
        // Here you can add your login logic, such as validating credentials
        // For simplicity, let's just pass the username to the parent component
        onLogin(username);
    };

    return (
        <form onSubmit={handleLogin}>
            <div><b>Volunteer Management Platform</b></div>
            <div className = 'header'>
                <div className = 'text'>{<b>Volunteer Management Platform</b>}</div>
                <div className = 'underline'></div>
            </div>
            <div>
                <input 
                    type="text" 
                    placeholder="Username" 
                    value={username} 
                    onChange={(e) => setUsername(e.target.value)} 
                />
            </div>
            <div>
                <input 
                    type="password" 
                    placeholder="Password" 
                    value={password} 
                    onChange={(e) => setPassword(e.target.value)} 
                />
            </div>
            <div>
                <button type="submit">Login</button>
            </div>
        </form>
    );
};

export default LoginForm;
